+
+"use client";
+
+import { useCallback } from "react";
+
+interface CounterProps {
+      title: string;
+      subtitle: string;
+      value: number;
+      onChange: (value: number) => void;
+}
+const Counter: React.FC<CounterProps> = ({ onChange, subtitle, title, value }) => {
+      const onAdd = useCallback(() => {
+            onChange(value + 1);
+      }, [onChange, value]);
